import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import { AppBar, SwipeableDrawer } from '@material-ui/core';
import Slide from '@material-ui/core/Slide';
import React, { useCallback, useEffect, useState } from 'react';
import useStyles from './style';
import Menu from '../menu/menu';
import { isMobile } from 'react-device-detect';

// eslint-disable-next-line react/prop-types
function CustomAppBar({refs}) {
	const [isDrawerOpen, setDrawerOpen] = useState(false);

	const classes = useStyles();
	const itemPressed =(ref)=>{
		console.log(ref);
		ref.current.scrollIntoView();

	};
	const menuItems=useCallback((style)=>{
		return (
			<div className={style}>

				<IconButton onClick={()=>{itemPressed(refs.heroRef);}}>
					<Typography variant="h6" component="div" className={classes.logo}>
        KH
					</Typography>
				</IconButton>
				<IconButton  onClick={()=>{itemPressed(refs.aboutRef);}}>
					<Typography className={classes.menuButton}>
              About
					</Typography>
				</IconButton>
				<IconButton>
					<Typography className={classes.menuButton}>
              Projects
					</Typography>
				</IconButton>
				<IconButton>
					<Typography className={classes.menuButton}>
              Contact
					</Typography>
				</IconButton>
			</div>

		);
	},[isMobile]);
	const [scrollPosition, setScrollPosition] = useState(0);
	const handleScroll = () => {
		const position = window.pageYOffset;
		setScrollPosition(position);
	};
	useEffect(() => {
		window.addEventListener('scroll', handleScroll);

		return () => {
			window.removeEventListener('scroll', handleScroll);
		};
	}, [window]);

	const appBarItems= useCallback(()=>{
		if(isMobile){
			return(
				<div className={classes.row}>
					<IconButton>
						<Typography variant="h6" component="div" className={classes.logo}>
        KH
						</Typography>
					</IconButton>

					<IconButton
						className={classes.menuButton}
						onClick={() => {
							setDrawerOpen(true);
						}}
						size="large"
						color="inherit"
						aria-label="menu"
					>
						<MenuIcon className={classes.menuIcon} />
					</IconButton>
				</div>
			);

		}else{

			return(
				<div className={classes.itemRow}>


				</div>
			);
		}
	},[isMobile]);

	return (
		<>
			<Slide apear={false} direction="down" in={scrollPosition >= 900}>
				<AppBar position="fixed" className={classes.appBar}>
					<Toolbar>

						{appBarItems()}
					</Toolbar>
				</AppBar>
			</Slide>

			<SwipeableDrawer
				anchor="right"
				open={isDrawerOpen}
				onClose={() => {
					setDrawerOpen(false);
				}}
			>
				<Menu onClose={()=>{setDrawerOpen(false);}}/>
			</SwipeableDrawer>
		</>
	);
}
export default CustomAppBar;
