import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  menu: {
    width: '300px',
    display: 'flex',
    flexDirection: 'column',
    flex: 1,
    backgroundColor: '#FFF0DB',
    position:'relative',
  },
  close:{   
      position:'absolute',
      top:10,
      left:"80%",
      width:"5%",
      height:"5%",

  },icon:{
      color:'black',
  },
  item:{
      textAlign:'center',
      padding: "10%",
  }
}));

export default useStyles;
