import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  menu: {
    width: '100%',
    height: '20%',
    display: 'flex',
    flexDirection: 'column',
    flex: 1,
    backgroundColor: '#FFF0DB',
  },
}));

export default useStyles;
