import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  aboutPage: {
    paddingTop: '20%',
    width: '100%',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    alignContent: 'space-between',
    backgroundColor: '#FFF0DB',
    height: '1000px',
  },
  textTitle: {
    fontFamily: 'Merriweather',
    fontWeight: 'bold',
  },
  text: {
    fontFamily: '5em Merriweather',
  },
  profileImg: {
    height: '95%',
    width: '95%',
    borderRadius: '30%',
    objectFit: 'cover',
  },
  profileBg: {
    height: '50%',
    width: '50%',
    maxWidth: '350px',
    maxHeight: '350px',

    minWidth: '320px',
    minHeight: '300px',

    borderRadius: '30%',
    backgroundColor: '#E4D5BD',
  },

  texts: {
    marginTop: '10%',
    width: '80%',
    height: '50%',
    maxWidth: '900px',
    textAlign: 'left',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-aound',
  },
  padding: {
    height: '10%',
    width: '100%',
  },
}));

export default useStyles;
