import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import { AppBar, SwipeableDrawer } from '@material-ui/core';
import Slide from '@material-ui/core/Slide';
import React, { useCallback, useEffect, useState } from 'react';
import useStyles from './style';
import Menu from '../menu/menu';
import { isMobile } from 'react-device-detect';

// eslint-disable-next-line react/prop-types
function DesktopAppBar({refs}) {
	const [isDrawerOpen, setDrawerOpen] = useState(false);

	const classes = useStyles();
	const itemPressed =(ref)=>{
		console.log(ref);
		ref.current.scrollIntoView();

	};
	const [scrollPosition, setScrollPosition] = useState(0);
	const handleScroll = () => {
		const position = window.pageYOffset;
		setScrollPosition(position);
	};
	useEffect(() => {
		window.addEventListener('scroll', handleScroll);

		return () => {
			window.removeEventListener('scroll', handleScroll);
		};
	}, [window]);


	return (
		<>

			<div className={classes.itemRow}>
				<IconButton onClick={()=>{itemPressed(refs.heroRef);}}>
					<Typography variant="h6" component="div" className={classes.logo}>
						{'KH'}
					</Typography>
				</IconButton>
				<IconButton  onClick={()=>{itemPressed(refs.aboutRef);}}>
					<Typography className={classes.menuButton}>
						{'About'}
					</Typography>
				</IconButton>
				<IconButton>
					<Typography className={classes.menuButton}>
						{'Projects'}
					</Typography>
				</IconButton>
				<IconButton>
					<Typography className={classes.menuButton}>
						{'Contact'}
					</Typography>
				</IconButton>
			</div>


		</>
	);
}
export default DesktopAppBar;
