import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  menu: {
    width: '300px',
    display: 'flex',
    flexDirection: 'column',
    flex: 1,
    backgroundColor: '#FFF0DB',
    position:'relative',
  },
  close:{
      position:'absolute',
      top:10,
      left:"90%",
      width:"5%",
      height:"5%",

      color:'red'

  },
  item:{
      textAlign:'center',
  }
}));

export default useStyles;
