import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme)=>({
    aboutPage:{
        width:"100%",
        position:'absolute',
        top:900,
        flex:1,
        alignContent: 'space-between',
        flexDirection:'column',
        paddingTop:"15%",
        backgroundColor:'#FFF0DB',
        height: "1000px",
    },
    profileImg:{
        height: 400,
        width:400,
        borderRadius:100,
        maxHeight: 400,
        maxWidth:400,

    },
    profileBg:{
        height: 420,
        width:420,
        borderRadius:100,
        maxHeight: 420,
        maxWidth:420,
        backgroundColor:'red',

    }

}));

export default useStyles;
