import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme)=>({
    aboutPage:{
        width:"100%",
        position:'absolute',
        top:900,
        flex:1,
        alignContent: 'space-between',
        flexDirection:'column',
        paddingTop:"15%",
        backgroundColor:'#FFF0DB',
        height: "1000px",
    },
    profileImg:{
        height: 200,
        width:200,
        borderRadius:100,
        position:'absolute',
        top:5,
        left:5,
        maxWidth:{xs:200, md:400},
        maxHeight: {xs:200, md:400},
        objectFit:"cover",

        maxBorderRadius:400,


    },
    profileBg:{
        height: 210,
        width:210,
        borderRadius:100,
        position:'absolute',
        top:0,
        left:"30%",
        maxWidth:{xs:210, md:410},
        maxHeight: {xs:210, md:410},
        backgroundColor:'#E4D5BD',

    },
    profile:{
        position:'relative',
        height:'50%',
        width:"100%"
    }


}));

export default useStyles;
