import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme)=>({
    aboutPage:{
        width:"100%",
        position:'absolute',
        top:900,
        flex:1,
        alignContent: 'space-around',
        flexDirection:'column',
        backgroundColor:'#FFF0DB',
        height: "1000px",
    },
    profileImg:{
        height:"100%",
        width:"100%",
        borderRadius:"25%",
        position:'absolute',
        top:"1%",
        left:"2%",
        maxWidth:{xs:300, md:400},
        maxHeight: {xs:300, md:400},
        objectFit:"cover",

        maxBorderRadius:400,


    },
    profileBg:{
        height: "50%",
        width: "50%",
        borderRadius:"25%",
        position:'absolute',
        top:0,
        left:"23%",
        maxWidth:{xs:310, md:410},
        maxHeight: {xs:310, md:410},
        backgroundColor:'#E4D5BD',

    },
    profile:{
        position:'relative',
        height:'50%',
        width:"100%"
    }


}));

export default useStyles;
