import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme)=>({
    aboutPage:{
        width:"100%",
        position:'absolute',
        top:900,
        flex:1,
        flexDirection:'column',
        backgroundColor:'#FFF0DB',
        height: "1000px",
    },
    profileImg:{
        height:"100%",
        width:"100%",
        borderRadius:"25%",
        objectFit:"cover",

        maxBorderRadius:400,


    },
    profileBg:{
        height: {md:"50%",xs:"40%"},
        width: {md:"20%",xs:"40%"},
        borderRadius:"30%",   
        backgroundColor:'#E4D5BD',

    },
    profile:{
        flex:1,
        height:"50%",
        width:"50%",
        maxHeight: {md:"200",xs:"100"},
        maxWidth:  {md:"200",xs:"100"},
    },

    texts:{
        flex:1
    }


}));

export default useStyles;
