import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme)=>({
    aboutPage:{
        width:"100%",
        position:'absolute',
        top:900,
        flex:1,
        flexDirection:'column',
        justifyContent:"center",
        alignItems:"center",
        alignContent:"center",
        backgroundColor:'#FFF0DB',
        height: "1000px",
    },
    profileImg:{
        position:'absolute',
        top:"35%",
        left:'55%',
        height:"90%",
        width:"90%",
        borderRadius:"25%",
        objectFit:"cover",

        maxBorderRadius:400,


    },
    profileBg:{
        position:'absolute',
        top:"30%",
        left:'50%',
        height:"100%",
        width:"100%",
        borderRadius:"30%",   
        backgroundColor:'#E4D5BD',

    },
    profile:{
        height:"30%",
        width:"50%",
        position:'relative',

    },

    texts:{
        flex:1
    }


}));

export default useStyles;
