import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme)=>({
    page:{
        height: "800px",
        width:"100%",
        position: "relative",
        backgroundColor: "black",
        textAlign: "center",
        justifyContent: "center",
        alignItems: "center",

    },
    container:{
        width:"70%",
        position:"absolute",
        top:0,
        left:"15%",
        height: "100%",
    },
    hero:{
        width:"100%",
        height: "100%",
        opacity: "0.5",
        objectFit:"cover",

    },
    h1:{
        textAlign: "start",
        width: "60%",
        top: "5%",
        left: "10%",
        color: "white",
        position: "absolute",
        font: "3em Merriweather"
    },
    h2:{
        textAlign: "start",
        width: "40%",
        top:"30%",
        left: "10%",
        color: "white",
        position: "absolute",
        font: "2em Merriweather",

    },
    button:{
        width: "17%",
        textAlign: "center",
        height: "5%",
        top:"30%",
        left: "10%",
        position: "absolute",
        color: "black",
        font: "1em Merriweather",
        backgroundColor:"#E4BFB7",
    }
}));

export default useStyles;
